import { DOBPersonV2, VCDOBPersonV2Type, getVCDOBPersonV2Context } from '../../dob/v2'
import { expandVCV2 } from '../__fixtures__'

describe('VCDOBPersonV2', () => {
  it('expands correctly', async () => {
    const expanded = await expandVCV2<VCDOBPersonV2Type, DOBPersonV2>({
      type: 'DOBCredentialPersonV2',
      data: {
        '@type': 'DOBPerson',
        birthDate: '2021-10-27T23:36:53.803Z',
      },
      context: getVCDOBPersonV2Context(),
    })

    expect(expanded).toMatchInlineSnapshot(`
      Object {
        "@id": "urn:uuid:9b1deb4d-3b7d-4bad-9bdd-2b0d7b3dcb6d",
        "@type": Array [
          "https://www.w3.org/2018/credentials#VerifiableCredential",
          "https://schema.bloom.co/DOBCredentialPersonV2",
        ],
        "https://www.w3.org/2018/credentials#credentialSubject": Array [
          Object {
            "https://schema.bloom.co/data": Array [
              Object {
                "@type": Array [
                  "https://schema.bloom.co/DOBPerson",
                ],
                "https://schema.org/birthDate": Array [
                  Object {
                    "@value": "2021-10-27T23:36:53.803Z",
                  },
                ],
              },
            ],
          },
        ],
        "https://www.w3.org/2018/credentials#holder": Array [
          Object {
            "@id": "did:example:123",
          },
        ],
        "https://www.w3.org/2018/credentials#issuanceDate": Array [
          Object {
            "@type": "http://www.w3.org/2001/XMLSchema#dateTime",
            "@value": "2021-05-20T00:00:00.000Z",
          },
        ],
        "https://www.w3.org/2018/credentials#issuer": Array [
          Object {
            "@id": "did:elem:EiD73F4C1VzN7w-bSUN26nRPOh0ohVSkPR0Lv2aybnkqZQ;elem:initial-",
          },
        ],
      }
    `)
  })
})
